const nav=Array.from(document.querySelectorAll(".navigation")),slider=document.querySelector(".slider_cont"),arrColor=["green","black","white","red","blue","pink","yellow","brown"];let count=0;for(let e=0;e<arrColor.length;e++){const r=document.createElement("div");r.classList.add("divBlock"),r.style.backgroundColor=arrColor[e],slider.append(r)}const sidescroll=e=>{const r=slider.children[0].clientWidth;slider.scroll({top:0,left:r*e,behavior:"smooth"})};setInterval(()=>{sidescroll(count),count<arrColor.length-1?count++:count=0},3e3),window.addEventListener("click",e=>{e.target.classList.contains("navigation")?(nav.map(e=>e.querySelector(".sub_menu").classList.remove("sub_menu--active")),e.target.querySelector(".sub_menu").classList.add("sub_menu--active")):Array.from(document.querySelectorAll(".sub_menu")).map(e=>e.classList.remove("sub_menu--active"))});